sys_security_acl.script:
// Par défaut : accès refusé
answer = false;

var userID = gs.getUserID();

// Admin → accès complet
if (gs.hasRole('admin')) {
    answer = true;
}

// Employee → uniquement ses réservations
else if (gs.hasRole('x_1829144_room.employee')) {
    answer = (current.requested_for == userID);
}

// Manager → réservations de ses employés
else if (gs.hasRole('x_1829144_room.manager')) {
    if (current.requested_for.manager == userID) {
        answer = true;
    }
}

// Technicien → réservations qui lui sont assignées
else if (gs.hasRole('x_1829144_room.technician')) {
	answer = true;
    //answer = (current.assigned_to == userID);
}
